package SelfPractice;

public class WhileloopBasic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//to Print number from 1 to 11
	int i=1;
	while(i<11)
	{
		System.out.println(i);  //While loop is used if we don't know fix number of iteration(not fixed iteration)
		i=i+1;
	}		
	}

}
